import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import { FaTrash } from "react-icons/fa";
import { FaEdit } from "react-icons/fa";

const Books = () => {
  const [books,setBooks] = useState([]);

  useEffect(()=>{
    const fetchAllBooks = async ()=>{
      try {
        const res = await axios.get("http://localhost:8800/books")
        setBooks(res.data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchAllBooks();
  })

const handleDelete = async (id) =>{
  try {
    await axios.delete("http://localhost:8800/book"+id);
    window.location.re
  } catch (err) {
    console.log(err);
  }
}

  return (
    <div>
      <h1 className='title'>Book Shop</h1>
      <div className='books flex gap-2'>{books.map(book=>(
        <div className='book flex-1 flex flex-col gap-3 items-center' key={book.id}>
            {book.cover && <img src={book.cover} alt=''/>}
            <h2 className=' text-xl font-bold'>{book.title}</h2>
            <p>{book.desc}</p>
            <span>{book.price}</span>
            <button className='delete text-red-500 flex flex-row gap-1 justify-center items-center border-red-500 hover:bg-red-500 hover:shadow-lg hover:shadow-red-300' onClick={()=>handleDelete(book.id)}>Delete <FaTrash size={16}/></button>
            <button className='update flex flex-row gap-1 justify-center items-center'>Update <FaEdit size={16}/></button>
        </div>
      ))}
      </div>
      <button className=' m-4'>
        <Link to={"/add"}>Add new Book</Link>
      </button>
    </div>
  )
}

export default Books
